/*============================================================================= # # Author: svtter - svtter@qq.com # # QQ : 57180160 # # Last modified: 2015-05-04 09:50 # # Filename: 11634.cpp # # Description: # =============================================================================*/ #include &lt;iostream&gt; #include &lt;string.h&gt; #include &lt;assert.h&gt; #include &lt;stdio.h&gt; #include &lt;cmath&gt; #include &lt;queue&gt; using namespace std; #define INF 0x3f3f3f3f #define ll long long int #define MEM(a) memset(a, 0, sizeof(a)) #define MEMM(a) memset(a, -1, sizeof(a)) #define DEB(x, n) cout &lt;&lt; (x) &lt;&lt; " " &lt;&lt; (n) &lt;&lt; endl #define FOR(i, s, e) for(int (i) = (s); (i) &lt; (e); (i)++) #define CR printf("\n") const double PI = acos(-1.0); const int maxn = 1010; char g[maxn][maxn]; int gex[maxn][maxn]; bool vis[maxn][maxn]; int r, c; int dx[] = {, 1, -1, }; int dy[] = {1, , , -1}; struct Node { int i, j, t; Node() {} Node(int i, int j, int t):i(i), j(j), t(t){} }; inline int judge(int x, int y) { return x &gt;= && x &lt; r && y &gt;= && y &lt; c; } void init(queue &lt;Node&gt; &q) { Node temp, next; int nx, ny; while(!q.empty()) { temp = q.front(), q.pop(); temp.t++; for(int i = ; i &lt; 4; i++) { nx = temp.i + dx[i]; ny = temp.j + dy[i]; if(g[nx][ny] != &#39;#&#39; && judge(nx, ny) && gex[nx][ny] &gt; temp.t) { next.i = nx; next.j = ny; next.t = temp.t; gex[nx][ny] = temp.t; q.push(next); } } } } int bfs(queue &lt;Node&gt; &q) { Node t, n; int nx, ny; while(!q.empty()) { t = q.front(), q.pop(); if(t.i == r-1 || t.j == c-1 || t.i == || t.j == ) return t.t; t.t ++; for(int i = ; i &lt; 4; i++) { nx = t.i + dx[i]; ny = t.j + dy[i]; if(!vis[nx][ny] && gex[nx][ny] &gt; t.t && judge(nx, ny)) { n.i = nx, n.j = ny, n.t = t.t; vis[nx][ny] = 1; q.push(n); } } } return ; } int main() { #ifdef DEBUG freopen("input", "r", stdin); //ä»inputæ–‡ä»¶ä¸­è¯»å…¥ // freopen("output", "w", stdout); //è¾“å‡ºåˆ°outputæ–‡ä»¶ #endif int t; scanf("%d", &t); queue &lt;Node&gt; q; while(t--) { while(!q.empty()) q.pop(); scanf("%d%d", &r, &c); MEM(vis); Node joe; memset(gex, INF, sizeof(gex)); for(int i = ; i &lt; r; i++) scanf("%s", g[i]); for(int i = ; i &lt; r; i++) { for(int j = ;j &lt; c;j ++) { if(g[i][j] == &#39;F&#39;) { vis[i][j] = 1; gex[i][j] = 1; q.push(Node(i, j, 1)); } else if(g[i][j] == &#39;J&#39;) { vis[i][j] = 1; joe = Node(i, j, 1); } else if(g[i][j] == &#39;#&#39;) vis[i][j] = 1; } } init(q); q.push(joe); int ans = bfs(q); if(ans == ) puts("IMPOSSIBLE"); else printf("%d\n", ans); } return ; }

